practica-1.codigo_ejemplo:
(define (costo-total-viaje ciudades costos)
  (if (or (null? ciudades) (null? costos))
      0
      (+ (car costos) 
         (costo-total-viaje (cdr ciudades) (cdr costos))
      )
  )
)

; Llamado a la función con los valores reales
(costo-total-viaje
  '(Roma Milano Venecia Florencia)
  '(150 200 180 120)
)

practica-3.codigo_ejemplo:
(define (suma-gastos lst)
  (if (null? lst)
      0
      (+ (car lst) 
         (suma-gastos (cdr lst))
      )
  )
)

(define (gasto-promedio gastos)
  (if (null? gastos)
      0
      (/ (suma-gastos gastos) (length gastos))
  )
)

; Llamado a la función con los valores reales
(gasto-promedio
  '(800 300 150 200)
)

practica-1.codigo_ejemplo:
let rec precio_total_libros libros =
  match libros with
  | [] -> 0
  | (_, precio) :: t ->
      precio +
      precio_total_libros t
;;

(* Llamado a la función con los valores reales *)
precio_total_libros
  ["CienAñosDeSoledad", 25;
   "ElQuijote", 30;
   "LaOdisea", 20;
   "DonJuan", 15];;

practica-2.codigo_ejemplo:
let rec peliculas_destacadas peliculas =
  match peliculas with
  | [] -> []
  | (nombre, nota) :: t ->
      if nota > 8 then
        nombre :: 
        peliculas_destacadas t
      else
        peliculas_destacadas t
;;

(* Llamado a la función con los valores reales *)
peliculas_destacadas
  ["Titanic", 9;
   "Avatar", 7;
   "Inception", 10;
   "Matrix", 8];;

practica-3.codigo_ejemplo:
let rec suma_con_descuento compras =
  match compras with
  | [] -> 0.0
  | h :: t ->
      (float_of_int h *. 0.9) +
      suma_con_descuento t
;;

(* Llamado a la función con los valores reales *)
suma_con_descuento
  [120;
   80;
   200;
   50];;

practica-2.codigo_ejemplo:
type producto = { nombre: string; precio: int; stock: int }

let valor_inventario p =
  let valor_unitario = p.precio in
  let cantidad = p.stock in
  valor_unitario * cantidad
in
valor_inventario { nombre = "Laptop"; precio = 1200; stock = 15 }

practica-3.codigo_ejemplo:
type sensor = { temperatura: float; humedad: float; presion: float }

let indice_confort s =
  let factor_temp = s.temperatura *. 0.6 in
  let factor_hum = s.humedad *. 0.3 in
  let factor_pres = s.presion /. 100.0 in
  factor_temp +. factor_hum +. factor_pres
in
indice_confort { temperatura = 22.5; humedad = 65.0; presion = 1013.25 }
